import React, { useState } from 'react'
import logo from '../../assets/logo.png'
import './Cabecera.css';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faSearch} from '@fortawesome/free-solid-svg-icons';

function Cabecera(){
    const [ isHidden , setHidden ] =  useState(false)

    const home = () => {
      setHidden(true);
    }

    const misFavoritos = () => {
      setHidden(false)
    }

    const cerrarSesion = () => {
      console.log("Todavia no estoy listo");
    }

    return (
      <div>
        <header className="cabecera">
            <img src={logo} className='logo'/>
            <button onClick={cerrarSesion} className='botonesMenu'>Cerrar sesión</button>
            <button onClick={misFavoritos} className='botonesMenu'>Mis favoritos</button>  
            <button onClick={home} className='botonesMenu'>Home</button>
            <FontAwesomeIcon icon={faSearch} className='botonesMenu' onClick={home}/>
            <input type="text" class="botonesMenu buscador" placeholder="Busque una película"></input>

        </header>
        {isHidden && <h1>Holaa</h1>}
      </div>
    );       
  }


  export default Cabecera;
